INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (635385, 'Felix', '[{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"Cannot get account on line to go through I have act code\"},{\"Felix\":\"Ok. Which browser are you using to go online?\"},{\"Customer\":\"Apple I pad\"},{\"Felix\":\"ok, please may you try using a laptop to see if you can get online there?\"},{\"Customer\":\"I only have apple I pad\"},{\"Felix\":\"ok, unfortunately for now we do not yet have an app for iPad, and we recommend users use laptops as we do not fully support ipads yet\"},{\"Felix\":\"are you using ipad safari or chrome?\"},{\"Customer\":\"Not sure\"},{\"Felix\":\"ok, it looks like you are using iOS12, which is a few years old. we\'d recommend updating your ipad to the latest iOS. however we cannot guarantee this will work as we recommend using laptops/desktop computers\"},{\"Customer\":\"Ok will phone Adrian flux and get document sent in post. Thank you.\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (642035, 'Steve', '[{\"Customer\":\"Hello\"},{\"Customer\":\"stumbled across your website\"},{\"Customer\":\"Hello Steve\"},{\"Steve\":\"Hi\"},{\"Customer\":\"I have been a Mitel engineer for over 20 years and never heard of talkative\"},{\"Customer\":\"you are big in Mitel\"},{\"Customer\":\"I see you have the Bravissimo account that use to be a 4Sight customer\"},{\"Steve\":\"Ah, that\'s a shame, we work with Mitel regularly although we aren\'t an OEM partner. We are part of Wesley Clover Group which is a Terry Matthews business also.\"},{\"Steve\":\"I\'m an old Mitel engineer also. I worked at Mitel from 1991 to 2016\"},{\"Customer\":\"you don\'t have Mitel engineers\"},{\"Customer\":\"the online stuff for Ignite you have made is very good\"},{\"Customer\":\"Mitel don\'t have anything like it\"},{\"Customer\":\"I have made my own but not as good as yours\"},{\"Steve\":\"Yes, we have a lot of Mitel customers. Our main customer base are Mitel MiCC customers looking to deploy something a bit more website capable than the standard Mitel chat. We intergrate into ignite delivering Chat / Voice / Video and Cobrowse into MiCC\"},{\"Steve\":\"Happy to arrange a demo sometime for you if you like\"},{\"Customer\":\"but you outsource this\"},{\"Customer\":\"have you got AI working\"},{\"Customer\":\"and if so which customer\"},{\"Customer\":\"and can I check out the bot online\"},{\"Customer\":\"I am looking to set up a demo but not getting any feedback from the field\"},{\"Customer\":\"so I imagine its not that easy\"},{\"Steve\":\"We sell via Mitel partners. We have spoken to 3fiftyNine, Zetacom, ICP, Yourizon, HPS....\"},{\"Customer\":\"never heard of them\"},{\"Customer\":\"Olive and 4Sight are two of the best in the UK\"},{\"Steve\":\"Ah, AI is an interesting one. We have integration for Dialogflow same as you. But Google do not provide Agent Assist to the rest of the market. So we can develop Virtual agent and Chatbot type offerings that handoff to Talkative agents if required\"},{\"Steve\":\"We have a process to work with Customer and Partner to develop AI opps. We are currently finishing projects for Formula 1 and Durham County Council. But nothing deployed yet\"},{\"Customer\":\"but not your guys\"},{\"Steve\":\"? Not sure what you mean, \\\"your guys\\\"\"},{\"Customer\":\"do you know ALC from Mitel\"},{\"Customer\":\"he is working on AI for Olive\"},{\"Steve\":\"Andrew, yes\"},{\"Steve\":\"We have similar capability in Dialogflow\"},{\"Customer\":\"I am trying to reach out to other that have started the journey as Mitel documention not that good\"},{\"Steve\":\"I recommend the Coursera course https://www.coursera.org/learn/conversational-experiences-dialogflow/home/welcome\"},{\"Customer\":\"do you get that stuff?\"},{\"Steve\":\"Yes, dialogflow is actually pretty easy once you know it. The challenge with all AI projects is the \\\"Conversation design\\\" that\'s a key part of a succesful AI deployment. It\'s the tricky bit\"},{\"Customer\":\"I was looking for a new challenge. I will dig into it\"},{\"Customer\":\"do you start with coursera or do I need to get demo up and running first???\"},{\"Customer\":\"thanks I better got on with some work now. but well done on the site. you need to get it out there on linkdin as I have not seen you.\"},{\"Steve\":\"lol, yes all the sales and partners should know about us. We are at Mitel Next regularly and work with the Mitel user group. But I will double down on Benelux. Good luck with your AI journey, it\'s a good skill to have\"},{\"Steve\":\"What\'s your name BTW and I\'ll add you on linkedin, don\'t doubt we have mutuals\"},{\"Customer\":\"john smith\"},{\"Steve\":\"Nice to meet you John, I\'ve added you, feel free to reach out anytime with queries\"},{\"Customer\":\"nice one, thanks\"},{\"Customer\":\"bye\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (645620, 'Felix', '[{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"Hello, in reference to this https://support.talkative.uk/technical/adding-scripts page, we have the script implemented for a client\"},{\"Customer\":\"However, this CSS file https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css seems to have no other dependencies on the page and it is found to be 100% unused across page templates for our client\"},{\"Customer\":\"Could this line of code \\\"<link rel=\\\"stylesheet\\\" href=\\\"https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css\\\">\\\" be removed from the script as it contributes to the site loading time but doesn\'t seem to be contributing much to the page?\"},{\"Felix\":\"Hi!\"},{\"Felix\":\"Thanks for getting in touch\"},{\"Felix\":\"Let me speak to a colleague quickly\"},{\"Felix\":\"The font-awesome script can be removed, however, there will be some missing icons from the chat widget\"},{\"Customer\":\"Ok thank you. Can you give us a screenshot example of what the missing icons would be and how this would look in practice?\"},{\"Felix\":\"bear with me, let me generate this screenshot for you\"},{\"Customer\":\"Happy to take this over to email or a phone call if that would be easier\"},{\"Felix\":\"This should not take more than a few minutes to generate\"},{\"Felix\":\"https://eu-engage-app.s3.eu-west-2.amazonaws.com/files/8b802cbb-b3a6-4789-a789-c72901d012fc\"},{\"Felix\":\"as you can see, the change is very minimal\"},{\"Felix\":\"it just removes some stylings\"},{\"Customer\":\"Is the one on the right with and the one on the left without?\"},{\"Felix\":\"the one on the left which has the chat bubble is with, and the one on the right is missing the chat bubble\"},{\"Customer\":\"Oh right I see, so essentially it is just the chat bubble?\"},{\"Felix\":\"most icons in the chat script have now been replaced with inline SVGs\"},{\"Felix\":\"Essentially yes, there are other features such as voice, video and cobrowse which have other relevant icons\"},{\"Customer\":\"& removing the font awesome line would also remove these other icons you mention?\"},{\"Felix\":\"yes it would\"},{\"Felix\":\"https://eu-engage-app.s3.eu-west-2.amazonaws.com/files/23f1ac08-63ff-46e4-ad7b-14d855a82f96\"},{\"Felix\":\"I have reattached the screenshot showing the presence of th eother icons\"},{\"Customer\":\"Thank you so much :) this is super helpful.\"},{\"Felix\":\"You are most welcome, is there anything else I can help with?\"},{\"Customer\":\"I think this is it thank you\"},{\"Felix\":\"Great, well, if there is anything else, please do get in touch, we will be happy to help :)\"},{\"Felix\":\"Take care, and have a great day! Goodbye\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (651668, 'Felix', '[{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"Hi Felix. I thin k I spoke to you the other day about this same issue. Regarding https://support.talkative.uk/technical/adding-scripts and removing the line of script <link rel=\\\"stylesheet\\\" href=\\\"https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css\\\"> to improve site performance\"},{\"Felix\":\"Hi there, yes please can I take your email so we can loop you into the email thread we have about this?\"},{\"Customer\":\"foo@bar.com\"},{\"Felix\":\"thank you, please may you confirm the client name just to be safe?\"},{\"Customer\":\"ABC\"},{\"Felix\":\"ok great thanks\"},{\"Felix\":\"if it\'s ok i\'ll email you now and you can see the other comments\"},{\"Customer\":\"Sure - that would be great thanks\"},{\"Felix\":\"no probs, thanks\"},{\"Customer\":\"would you be able to just ping me when you have forwarded the email chain?\"},{\"Customer\":\"Ah I\'ve got it, thanks very much!\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (654874, 'Steve', '[{\"Steve\":\"Hello! Welcome to the Talkative website. My name is Steve. How can I help you?\"},{\"Customer\":\"Hi Steve\"},{\"Customer\":\"How are you?\"},{\"Steve\":\"Wonderful thanks. Who am I talking to ?\"},{\"Customer\":\"My name is Glenn Walker.\"},{\"Customer\":\"I was just wondering, do you think football would be better if the quadrant for corner kicks was bigger?\"},{\"Steve\":\"Well glad you asked Glenn because that\'s been a bugbear of mine for a long time. When are you commentating next?\"},{\"Steve\":\"Not biased at all\"},{\"Customer\":\"Hopefully on this evening\'s Spurs match. I\'m looking for some jazzy ideas so I don\'t sound so boring?\"},{\"Steve\":\"It;s too late for that Glenn\"},{\"Customer\":\"Ah okay. Thanks for the kind feedback. You have a good day Steve. Good talking with you.\"},{\"Steve\":\"thanks\"},{\"Steve\":\"you still here Glenn. Anything I can help you with?\"},{\"Customer\":\"Nope. I think I\'m good from here on. Thank you.\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (663861, 'Felix', '[{\"Customer\":\"Hey Felix\"},{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"I am shopping around for a live chat service for the College i work for. We would like multiple live chat options for different webpages while also integrating with Salesforce\"},{\"Felix\":\"We can definitely help with that. You can customize the live chat behaviour/UI on a per page basis\"},{\"Felix\":\"We are on the AppExchange and have a \\\"native\\\" Salesforce integration\"},{\"Customer\":\"Awesome sounds good so far\"},{\"Felix\":\"If you\'d like to set up a demo, you can do so on our website. Or I can take your email now and send you some more info over\"},{\"Customer\":\"Regarding your pricing options, does 3 user limit to how many chat boxes we can have?\"},{\"Felix\":\"No, in theory you could have 1 user and 100 chat designs. From a pricing perspective, we have our licence cost (per user) but this doesn\'t limit the number of chat designs\"},{\"Felix\":\"How many chat boxes are you thinking of?\"},{\"Customer\":\"at least 7 different chat boxes\"},{\"Felix\":\"ok\"},{\"Customer\":\"So will the user just have to be logged into their Salesforce account to interact in the chat boxes?\"},{\"Felix\":\"The chat widget will show as \\\"online\\\" if 1. you\'re within office hours that are set in the platform, and 2. if you have agents online\"},{\"Felix\":\"when the widget is offline, you can hide the widget or show an email collector form\"},{\"Customer\":\"Okay cool\"},{\"Felix\":\"If you want more info feel free to reach out at <email>\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (672991, 'Felix', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Hi\"},{\"Talkative\":\"Hi! How are you doing?\"},{\"Customer\":\"Good thanks\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"HI\"},{\"Customer\":\"My name is Paul, I am Head of Sales for company-name.co.uk\"},{\"Customer\":\"We would like to take the Free Trial\"},{\"Customer\":\"During the Trial can i connect to Mitel Ignite?\"},{\"Felix\":\"Hi Paul, you sure can\"},{\"Customer\":\"perfect thank you.\"},{\"Felix\":\"Let me know your email and I\'ll set you up with an account now\"},{\"Customer\":\"<email>\"},{\"Felix\":\"Got it thanks. There is a bit of set up to do with your Mitel system to enable Talkative integration. I\'ll email through the set up guide, typically your Mitel partner would do it for you\"},{\"Customer\":\"Thank you, the guide will be useful, I will send this over to our IT team\"},{\"Felix\":\"Sending the email over now, thanks\"},{\"Customer\":\"Ok. I have not received the email yet, let me know when its sent.\"},{\"Felix\":\"sent\"},{\"Customer\":\"Thank you\"},{\"Customer\":\"I will speak with out IT and Web team and get the Trial going asap\"},{\"Customer\":\"thanks for your help today.\"},{\"Felix\":\"no problems, speak soon!\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (673143, 'Felix', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"hi, do you have a screenshot of how cobrowsing works?\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Felix\":\"Hi, what kind of screenshot are you looking for?\"},{\"Customer\":\"of how a customer see a cobrowsing session\"},{\"Felix\":\"would you like me to send through a picture, or we can cobrowse now?\"},{\"Customer\":\"we can cobrowse now\"},{\"Customer\":\"so i can see it\"},{\"Felix\":\"so now i can see your screen and annotate the page\"},{\"Customer\":\"ok, how exaclty i can see your cursor, for example\"},{\"Customer\":\"is a bit difficult to see the difference\"},{\"Felix\":\"are you looking to use cobrowse for your company?\"},{\"Customer\":\"yes\"},{\"Customer\":\"but i need to see how a customer will see it\"},{\"Felix\":\"ok\"},{\"Customer\":\"oh, ok i see it\"},{\"Felix\":\"so on my side i can choose to either draw, or show my mouse to you\"},{\"Felix\":\"or click on links\"},{\"Customer\":\"great, thanks\"},{\"Customer\":\"thats all what i need\"},{\"Customer\":\"have a good day\"},{\"Felix\":\"https://eu-engage-app.s3.eu-west-2.amazonaws.com/files/95cfe7a1-c72a-41b0-9d99-e005b6814287\"},{\"Felix\":\"ok thanks!\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (679458, 'Steve', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Hi There  - I am working on a project and looking at alternatives for webchat  and came across the video for talkative\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Customer\":\"we are using Mitel and looking webchats which integrate with the contact centre Ignite\"},{\"Steve\":\"Hello! Welcome to the Talkative website. My name is Steve. How can I help you?\"},{\"Steve\":\"Sorry for the delay, our Chatbot shouldn\'t have let you get through till we were available\"},{\"Steve\":\"Happy to discuss your requirement further here or you can contact me at swilliams@talkative.uk\"},{\"Customer\":\"hi Steve\"},{\"Customer\":\"are you still there\"},{\"Steve\":\"yes, again, sorry to keep you waiting. We aren\'t staffed for your timezone as you can probably tell.\"},{\"Steve\":\"Are you a customer or a Mitle partner?\"},{\"Steve\":\"Mitek\"},{\"Customer\":\"I am a contractor for a company that is using Mitel and Ignite\"},{\"Customer\":\"and we are looking at options for chat\"},{\"Customer\":\"and came across the talkative\"},{\"Customer\":\"but a few people are keen to use Zendesk\"},{\"Steve\":\"understand, yes we provide Chat, Webvoice, Webvideo and Cobrowse. Importantly delivered directly into Ignite so you can blend the website interactions with and Voice / Email you may be handling in Mitek today\"},{\"Steve\":\"Are you able to share the company or sector you are contracting for?\"},{\"Steve\":\"Happy to arrange a demonstration sometime where we can show you the seamless working with Mitel\"},{\"Customer\":\"Hospitality sector -\"},{\"Customer\":\"i might get a demo as it does look good  -but I need to do a summary of features and benefits\"},{\"Steve\":\"Ok, share me your email and I will send you over some info\"},{\"Customer\":\"should I take your details and follow up\"},{\"Customer\":\"<email>\"},{\"Customer\":\"thanks\"},{\"Steve\":\"Ok Melissa, I will send you an email in 5 minutes. Ding me back here if it doesn\'t get through. And then let me know when you want to follow up. We are UK based so timezones aren\'t ideal but I can accomodate a suitable time for you with a few days notice\"},{\"Customer\":\"ok cool\"},{\"Customer\":\"I noticed your comartive section on the website but noticed that each one is not always comparing like with like\"},{\"Customer\":\"e.g. data security is not listed on the LIveperson comparison but is on the Livechat\"},{\"Steve\":\"There\'s not an ideal comparison unfortunately however our integration with Mitel is unique and most other platform are only providing Voice, Video and Cobroese with plugins at additional cost. And usually not integrated well\"},{\"Customer\":\"good to know\"},{\"Steve\":\"I\'ll send info over but let me know of any questions or further info we can provide and endeavour to get that to you too\"},{\"Customer\":\"as you are in the UK - what is the support provided?  Would it be through the partner that we have in Melbourne?\"},{\"Customer\":\"thanks Steve\"},{\"Steve\":\"Yes for Mitel we typically use a local partner. But it\'s not essential, we can deliver our solution directly however there may be work on the Mitel needed\"},{\"Steve\":\"We use Open Media which requires at least 9.2 MiCC and relevant multimedia licences. Also you neeed to configure the Openmedia connection.\"},{\"Customer\":\"i am trying to scroll back to a part of the chat as you mentioned something earlier and I did not read the full note\"},{\"Customer\":\"is there a way I can go back to begining of chat\"},{\"Steve\":\"you should have scroll bars. Not sure why you can\'t access them\"},{\"Customer\":\"it does not seem to be letting me\"},{\"Customer\":\"yes I do have a scroll bar but it keeps jumping ahead\"},{\"Steve\":\"I can see you are using Chrome on a Windows 10 machine. That should be all fine\"},{\"Steve\":\"I\'ll take a look , 1 second\"},{\"Steve\":\"I\'m just sending in a test chat to myslef.\"},{\"Customer\":\"it is ok - i found it - it was just in regards to combining chat with the email and voice from Mitel\"},{\"Customer\":\"I am not sure they would want to change the ignite voice and email - they are just not super sold on the Ignite chat functionality so wanted to look at options\"},{\"Customer\":\"let me know when you send through the mail and I will let you know\"},{\"Steve\":\"Ok, yes Talkative can be used Standalone or embedded into Ignite. Is it Ignite they don\'t like? Or Mitel\'s chat tool? When you use Talkative we replace the Mitel chat with our own\"},{\"Customer\":\"Mitel chat tool\"},{\"Steve\":\"ok, so worth a demo to show the enhanced chat we provide, combined with cobrowse and option to escalate to realtime with Voice / Video\"},{\"Customer\":\"yes that is what I thought ,  We would look to keep the email and voice component and look to use the chat and other social media integrations\"},{\"Customer\":\"yes definitely need to arrange and demo\"},{\"Customer\":\"I think probably some time next week - will need to confirm  times\"},{\"Customer\":\"how long would a demo take?\"},{\"Steve\":\"usually 45 minutes\"},{\"Customer\":\"ok thanks\"},{\"Steve\":\"sending the email now\"},{\"Customer\":\"thanks a lot Steve\"},{\"Steve\":\"you\'re welcome, again, sorry for the delay. I\'ve got my best man working on it as you shouldn\'t have been able to get into queue without someone there to talk to you\"},{\"Steve\":\"Yes, the chatbot was new last week and should follow our business hours. We think we had a test agent logged in somewhere that overrode it all\"},{\"Customer\":\"i did wonder what was happening but then went off and did a few other things so not problem but it would be good to now how it happened.  I noticed that it say I was the next in queue but it does not mention time.  Is that an option to set up? just an estimate wait time?\"},{\"Steve\":\"We can do position in queue for Mitel integrations. Not time\"},{\"Customer\":\"ok - thanks\"},{\"Steve\":\"Also widget on website should only appear in business hours and when the max number of queueing callers is in queue. Something failed today\"},{\"Customer\":\"technology .... never 100% perfect :)\"},{\"Customer\":\"stay safe and well and I will be in touch soon\"},{\"Steve\":\"yes, not like me and you Melissa\"},{\"Customer\":\"lol\"},{\"Steve\":\"No problem ,look forward to meeting online sometime soon. Stay safe\"},{\"Customer\":\"you too - thanks a bunch for all your help today\"},{\"Steve\":\"no trouble. have a good day / evening\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (681386, 'Felix', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Support\"},{\"Talkative\":\"If you\'re have a technical issue, please email support@talkative.uk or transfer to the team. Please note we have a support portal here for FAQs: https://support.talkative.uk/\"},{\"Customer\":\"Sprechen Sie deutsch?\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Bist du ein Bot?\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Customer\":\"Hallo\"},{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"Sprechen Sie deutsch?\"},{\"Felix\":\"Yes\"},{\"Customer\":\"Sind Sie ei nBot?\"},{\"Felix\":\"No! My name is Felix\"},{\"Customer\":\"Auch Bots können Namen haben.\"},{\"Customer\":\"Fängt der Live Chat mit einem Bot an?\"},{\"Felix\":\"You can either start with a bot, or go straight through to a human agent queue\"},{\"Felix\":\"I am using real-time translation feature, apologies if my German isn\'t accurate!\"},{\"Customer\":\"Das ist leider schlecht. Vielen Dank.\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (682477, 'Steve', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Steve\":\"Hello! Welcome to the Talkative website. My name is Steve. How can I help you?\"},{\"Customer\":\"Hi Steve!!!\"},{\"Customer\":\"Thanks for your help\"},{\"Customer\":\"I want to try all your functionality integrated with my salesforce\"},{\"Customer\":\"Live chat, call, sharing, co browsing for our online journey experience\"},{\"Customer\":\"Is possible to do that ?\"},{\"Customer\":\"Can we have a demo?\"},{\"Customer\":\"So fast as you can do\"},{\"Customer\":\"We want to integrate with our website, salesforce, an our App\"},{\"Customer\":\"company-site.com\"},{\"Customer\":\"Investment\"},{\"Steve\":\"Ok great, thanks for sharing that information. yes a Demo and chat may be the best way to proceed.\"},{\"Customer\":\"Maybe we can have a video call?\"},{\"Steve\":\"Of course. Can you share your availability for Thursday or Friday this week?\"},{\"Customer\":\"Sure, hold on please\"},{\"Steve\":\"No problem\"},{\"Customer\":\"May be 2:00 pm Mexico Time?\"},{\"Customer\":\"Thursday?\"},{\"Steve\":\"Yes that\'s fine for us. Can you let me know your email address and I will send you a Zoom invite\"},{\"Customer\":\"<email>\"},{\"Steve\":\"Ok, thanks you Emilio. Nice to meet you. I will send an invite now. I will show you to the Talkative Chat, Video and Cobrowse working in Salesforce and you can ask any questions you may have\"},{\"Customer\":\"Perfect!!!\"},{\"Customer\":\"In advanced it is possible to get a Qoute for 30 advisors?\"},{\"Steve\":\"You should have an invite now\"},{\"Customer\":\"Thank you!!\"},{\"Customer\":\"I don\'t have it yet, but I gess is on the way\"},{\"Steve\":\"Hi Emilio, for Budget reasons