INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (730006, 'Steve', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"hi\"},{\"Talkative\":\"Hello! How can I help you?\"},{\"Customer\":\"can i get brochure talking about Mitel Contact Centre Integration\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Mitel Contact Centre Integration brochure or presentation\"},{\"Talkative\":\"Sorry, I didn\'t quite catch that. Please rephrase your question, or click below to speak to the team:\"},{\"Customer\":\"Speak to the team\"},{\"Talkative\":\"Sure, will transfer you now!\"},{\"Steve\":\"Hi, you are through to Steve. Can see you are interested in Mitel integration. Are you a customer or Mitel partner?\"},{\"Customer\":\"a potential customer\"},{\"Steve\":\"Ok, do you use Mitel Contact Center Business?\"},{\"Customer\":\"yes\"},{\"Steve\":\"Ok this may be a good reference for you. https://www.youtube.com/watch?v=Px4aMnMHtBI\"},{\"Steve\":\"Do yuo have a website that you want to use Talkative on?\"},{\"Customer\":\"planning to come up with one\"},{\"Customer\":\"also want a document or brochure that i can present to my line manager please\"},{\"Steve\":\"Ok I can send you one via email. Can you share your address with me?\"},{\"Customer\":\"<email>\"},{\"Customer\":\"will really appreciate\"},{\"Steve\":\"No problem, that;s on the way. You have my contact details there. Get in touch if you would like to arrange a demonstration or further discussion\"},{\"Steve\":\"FYI, as well as Mitel integration you can also use Talkative within Salesforce and we can also deploy as a Standlaone application, all you need is a web browser.\"},{\"Customer\":\"ok noted\"},{\"Steve\":\"Do you have any other questions today?\"},{\"Customer\":\"just a moment\"},{\"Customer\":\"am just reading thro the doc\"},{\"Steve\":\"no problem\"},{\"Customer\":\"do you have a more detailed technical proposal\"},{\"Steve\":\"We would need to understand more about your environment. But you can review our help files at https://support.talkative.uk/\"},{\"Customer\":\"thanks Steve\"},{\"Customer\":\"let me go through shared info\"},{\"Customer\":\"much appreciated\"},{\"Customer\":\"if i need a demo will come back\"},{\"Steve\":\"ok, thanks, have a good day\"},{\"Customer\":\"have a good day too\"},{\"Customer\":\"bye for now\"}]');
