INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (999261, 'Steve', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Support\"},{\"Talkative\":\"If you\'re have a technical issue, please email support@talkative.uk or transfer to the team. Please note we have a support portal here for FAQs: https://support.talkative.uk/\"},{\"Customer\":\"I\'m trying to test my setup by being logged in as a user in 1 browser and editing the chat widget with an account holder in a different browser\"},{\"Talkative\":\"You can learn about our Chat solution on our website https://gettalkative.com/live-chat if you have further question please choose Speak to the Team. If we aren\'t currently available then please leave some contact details so we can get back to you.\"},{\"Customer\":\"But the test widget says no agents available?\"},{\"Talkative\":\"Sure, we will transfer you now! In case we aren\'t currently available can you also share your email address here?\"},{\"Customer\":\"<email>\"},{\"Steve\":\"Hi Douglas it\'s Steve here\"},{\"Customer\":\"Hi Steve\"},{\"Steve\":\"If you go to Queue\'s look at the Red X on the left and it will tell you why the queue is not available\"},{\"Steve\":\"In Settings that is\"},{\"Customer\":\"It says no agents available?\"},{\"Customer\":\"But i\'m logged in and available\"},{\"Customer\":\"The agent is in the group and assigned to the queue\"},{\"Steve\":\"Can you let me know the Queue name and Agent name you are logged in with\"},{\"Customer\":\"stewie Smith :)\"},{\"Customer\":\"In the SST queue\"},{\"Steve\":\"Checking with support team, 1 sec\"},{\"Customer\":\"Unfortunately i can\'t put the widget on the website as our dev who deals with it is on annual leave\"},{\"Customer\":\"so just testing through the portal widget section as Felix said i could do that\"},{\"Steve\":\"yeah you should be able to. Are you using SSO?\"},{\"Customer\":\"i am yeah\"},{\"Customer\":\"set it up this morning\"},{\"Customer\":\"I\'m logged in to the engage portal and ignite\"},{\"Customer\":\"do I need to be logged into both?\"},{\"Steve\":\"No, just ignite. We check the queue via the Openmedia API so it must be Mitel telling us the Queue has no one available. Can you check your Mitel config\"},{\"Steve\":\"Afraid we can only base it on what Mitel API tells us\"},{\"Steve\":\"This SST queue is linked to \\\"Talkative - SST\\\" Mitel queue right?]\"},{\"Customer\":\"yeah\"},{\"Customer\":\"that\'s right\"},{\"Steve\":\"Yeah, that queue is telling us no Agents\"},{\"Steve\":\"You need to use an Agent that is logged in, present and available in the \\\"Talkative - SST\\\" openmedia Queue\"},{\"Customer\":\"I\'m logged into ignite with a test agent and available\"},{\"Customer\":\"I can see the Talkative - SST queue\"},{\"Customer\":\"I\'m also online in the talkative portal\"},{\"Steve\":\"Ok, jump on here and let\'s share your screen Stephen Williams is inviting you to a scheduled Zoom meeting. https://us02web.zoom.us/j/6812471204\"},{\"Steve\":\"Hi Doug, Paget 70 in this doc if it helps https://www.mitel.com/en-gb/document-center/applications/contact-center/micontact-center-business/micontact-center-business-for-mivb/93-sp1/en/micontact-center-business-multimedia-installation-and-deployment-guide\"},{\"Customer\":\"Just removed and re-added to the agent group\"},{\"Customer\":\"i can see the group in ignite now\"},{\"Steve\":\"ah ok, may take 30-60 seconds for mitel to update persence to us\"},{\"Steve\":\"presence\"},{\"Customer\":\"Always found YSE to be a bit buggy\"},{\"Customer\":\"Thanks for your help\"},{\"Steve\":\"no problem\"},{\"Customer\":\"Hi Steve\"},{\"Customer\":\"Are you still there?\"},{\"Steve\":\"yes Douglas]\"},{\"Customer\":\"So i am in the open media queue, can see it in ignite and i\'m available\"},{\"Steve\":\"Still there Douglas?\"},{\"Customer\":\"hi Steven\"},{\"Customer\":\"sorry\"},{\"Customer\":\"internet issues there\"},{\"Customer\":\"Yes, so i\'m available in ignite on open media\"},{\"Customer\":\"but still nothing\"},{\"Steve\":\"ok jump back on Zoom again? https://us02web.zoom.us/j/6812471204\"},{\"Steve\":\"Cheers Doug, good luck with Commsworld, let us know what it is if it\'s something we have all missed. But I suspect there is something gone awry there that either a reboot or re-program won\'t solve.\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (1005906, 'Felix', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"Hello!\"},{\"Talkative\":\"Hello! How can I help you?\"},{\"Customer\":\"I need update\"},{\"Talkative\":\"I\'ll just get one of our product experts to help you, please wait one moment while we connect you\"},{\"Customer\":\"How do I contact them\"},{\"Felix\":\"hi\"},{\"Felix\":\"how can i help you?\"},{\"Customer\":\"How are you doing today\"},{\"Customer\":\"I need updates\"},{\"Felix\":\"updates on what sorry?\"},{\"Customer\":\"Sorry it was a mistake\"},{\"Customer\":\"On how to catch a real client\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (1008040, 'Steve', '[{\"Steve\":\"Hi Douglas, it\'s Steve\"},{\"Customer\":\"Hi Steve\"},{\"Customer\":\"Is there anywhere in the talkative portal that the chat widget is customized like adding an agent photo?\"},{\"Customer\":\"I notice you guys had that on your site\"},{\"Customer\":\"Or is that just a developer thing on our side?\"},{\"Steve\":\"Hi Doug, we are using some Custom CSS and HTML to deliver that feature using our legacy script engine. We have the new ECS2 scripts which provides the self service interface. I\'m not sure if you have that added to your account or not (It\'s due for full release in a few weeks) The ECS2 interface let\'s you add images and videos and such using a \\\"No code\\\" interface. See if this link works on your system https://eu.engage.app/chat-widgets\"},{\"Steve\":\"Looking back at emails I think Felix has sent you some info on the new Widget designer\"},{\"Customer\":\"Yeah - i\'ve been using that\"},{\"Customer\":\"Is that getting updated?\"},{\"Customer\":\"Or is that the updated one?\"},{\"Steve\":\"The one on our website uses the legacy scripts. As well as this new widget designer the ECS 2 scripts will be more efficient, secure and will have newer features added over the course of the next year. ECS2 isn\'t feature parity with legacy scripts today but we add Video on Thursday and you should see a new Cobrowse in a week or two\"},{\"Steve\":\"That the new one which you will see updates to every week over the next few weeks. Each Thursday prob worth logging into Engage and going to that page\"},{\"Steve\":\"https://eu-engage-app.s3.eu-west-2.amazonaws.com/files/cf19b780-4dfb-40c4-b7a6-593e1669be8d\"},{\"Steve\":\"If there is an update when you click \\\"Version\\\" it will prompt you to update your scripts\"},{\"Customer\":\"on the latest version is says\"},{\"Steve\":\"If you need any help setting that up let me know and we can have a Zoom to go over anything you want to do. May be worth waiting till Thursday when latest update is out. Lots of things changing in that part of the platform so your seeing it a bit early.\"},{\"Customer\":\"OK - will give you a shout Thursday\"},{\"Customer\":\"Cheers Steve\"},{\"Steve\":\"No problem\"},{\"Steve\":\"Anything else? You were looking at Callback I can see\"},{\"Customer\":\"I\'m speaking to commsworld re setting up the workflow\"},{\"Customer\":\"Not too sure what the settings should be\"},{\"Customer\":\"Nothing else for now though\"},{\"Customer\":\"Cheers\"},{\"Steve\":\"ok, have a good day\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (1009492, 'Felix', '[{\"Talkative\":\"Hi, welcome to Talkative! How can we help? Please ask us anything, or click the buttons below. Let us know if you\'d prefer to speak to a human!\"},{\"Customer\":\"wordpress is supported or not\"},{\"Talkative\":\"If you\'re have a technical issue, please email support@talkative.uk or transfer to the team. Please note we have a support portal here for FAQs: https://support.talkative.uk/\"},{\"Customer\":\"hey?\"},{\"Talkative\":\"Hi! How are you doing?\"},{\"Customer\":\"hahahha\"},{\"Talkative\":\"I\'ll just get one of our product experts to help you, please wait one moment while we connect you\"},{\"Felix\":\"Hi yes we support Wordpress\"},{\"Customer\":\"so do you offer a plugin or what\"},{\"Customer\":\"?\"},{\"Felix\":\"no plugin but we do support it\"},{\"Customer\":\"ok. so we can integrate it into our wordpress website\"},{\"Customer\":\"also do you offer a call back facility from your dashboard\"},{\"Felix\":\"yes and yes!\"},{\"Customer\":\"so the prices start from 250 $ a month or is there a lesser package\"},{\"Felix\":\"at the moment that is our lowest package\"},{\"Felix\":\"how many users are you looking for?\"},{\"Customer\":\"its currently not decided yet. but you can tell me if there is any lesser package available\"},{\"Customer\":\"also its a contract based system?\"},{\"Customer\":\"we are specifically looking for wordpress\"},{\"Felix\":\"if you have a lower number of users then we could maybe discount a bit, but we\'d want to understand that first. it\'s a 30 day rolling contract\"}]');
INSERT INTO chat_record (chat_record_id, agent_name, transcript) VALUES (1015028, 'Felix', '[{\"Felix\":\"Hello! Welcome to the Talkative website. My name is Felix. How can I help you?\"},{\"Customer\":\"Hi Felix nice to meet you\"},{\"Customer\":\"I am Constantinos Senior Developer for Company\"},{\"Customer\":\"We are trying to integrate your platform in our system for one of our clients\"},{\"Felix\":\"Is this FQ holdings?\"},{\"Felix\":\"Sure, how can i help\"},{\"Customer\":\"yes\"},{\"Customer\":\"I need information on how to get the company-uuid and queue-uuid\"},{\"Customer\":\"to setup the CDN\"},{\"Felix\":\"you can get this on the script generator page here https://eu.engage.app/script-generator\"},{\"Felix\":\"however\"},{\"Felix\":\"we have recently launched our new website widget which will make deploying the chat service a lot easier/faster\"},{\"Felix\":\"which you can find here: https://eu.engage.app/chat-widgets\"},{\"Customer\":\"ok\"},{\"Customer\":\"so the chat-widget will generate the code in the end of the wizard?\"},{\"Felix\":\"yes\"},{\"Felix\":\"the new chat widget will let you contriol the look/feel/design of the widget purely within the editor\"},{\"Customer\":\"we need chat,voice and screen share though\"},{\"Customer\":\"not only chat\"},{\"Customer\":\"is there a wizard for all three?\"},{\"Customer\":\"I guess we can use script generator for this right?\"},{\"Felix\":\"ok, the new widget currently supports chat, voice video and screenshare. however it currently only supports \'direct\' voice/video, you cannot currently escalate from chat to voice/video/share\"},{\"Felix\":\"however this is expected to be ready in 2-3 weeks\"},{\"Felix\":\"so depending on the clients timelines i\'d recommend using the new widget\"},{\"Customer\":\"deadline is strict so I dont think they will wait until then\"},{\"Customer\":\"the callback functions are the same though?\"},{\"Customer\":\"only the initialization is different?\"},{\"Felix\":\"ok when is